import React, { useState, useEffect } from 'react';
import { fetchImages } from './services/unsplashApi';
import InfiniteScroll from 'react-infinite-scroll-component';
import Modal from 'react-modal';
import './App.css';

Modal.setAppElement('#root');

const App = () => {
  const [images, setImages] = useState([]);
  const [query, setQuery] = useState('');
  const [page, setPage] = useState(1);
  const [selectedImage, setSelectedImage] = useState(null);
  const [hasMore, setHasMore] = useState(true);

  const searchImages = async () => {
    const newImages = await fetchImages(query, 1);
    setImages(newImages);
    setPage(2);
    setHasMore(true);
  };

  const loadMoreImages = async () => {
    const newImages = await fetchImages(query, page);
    setImages((prevImages) => [...prevImages, ...newImages]);
    setPage(page + 1);
    if (newImages.length === 0) setHasMore(false);
  };

  return (
    <div className="app">
      <h1>Image Search App</h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search for images..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={searchImages}>Search</button>
      </div>

      <InfiniteScroll
        dataLength={images.length}
        next={loadMoreImages}
        hasMore={hasMore}
        loader={<h4>Loading...</h4>}
        endMessage={<p>No more images</p>}
      >
        <div className="image-grid">
          {images.map((img) => (
            <img
              key={img.id}
              src={img.urls.small}
              alt={img.alt_description}
              onClick={() => setSelectedImage(img.urls.regular)}
              className="image-item"
            />
          ))}
        </div>
      </InfiniteScroll>

      {selectedImage && (
        <Modal
          isOpen={!!selectedImage}
          onRequestClose={() => setSelectedImage(null)}
          className="modal"
          overlayClassName="overlay"
        >
          <img src={selectedImage} alt="Full-size" className="modal-image" />
          <button onClick={() => setSelectedImage(null)} className="close-button">
            Close
          </button>
        </Modal>
      )}
    </div>
  );
};

export default App;
