import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import { fetchImages } from "./services/unsplashApi";
import { ThreeDots } from "react-loader-spinner";

Modal.setAppElement("#root");

export default function App() {
  const [images, setImages] = useState([]);
  const [query, setQuery] = useState("");
  const [page, setPage] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [hasMore, setHasMore] = useState(false);

  useEffect(() => {
    if (!query) return;

    const fetchImageData = async () => {
      setIsLoading(true);
      const results = await fetchImages(query, page);
      if (results.length > 0) {
        setImages((prevImages) => [...prevImages, ...results]);
        setHasMore(true);
      } else {
        setHasMore(false);
      }
      setIsLoading(false);
    };

    fetchImageData();
  }, [query, page]);

  const handleSearch = (e) => {
    e.preventDefault();
    setImages([]);
    setPage(1);
    setHasMore(false);
    setQuery(e.target.elements.search.value.trim());
  };

  const loadMoreImages = () => {
    setPage((prevPage) => prevPage + 1);
  };

  const openModal = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  const closeModal = () => {
    setSelectedImage(null);
  };

  return (
    <div>
      {/* Пошук */}
      <header style={{ backgroundColor: "#3f51b5", padding: "20px", textAlign: "center" }}>
        <form onSubmit={handleSearch}>
          <input
            type="text"
            name="search"
            placeholder="Search images..."
            style={{ padding: "10px", width: "300px", borderRadius: "4px", border: "none" }}
            required
          />
          <button
            type="submit"
            style={{
              padding: "10px 15px",
              marginLeft: "10px",
              backgroundColor: "#303f9f",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Search
          </button>
        </form>
      </header>

      {/* Галерея */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(4, 1fr)", // 4 колонки
          gap: "20px",
          padding: "20px",
        }}
      >
        {images.map((image) => (
          <img
            key={image.id}
            src={image.urls.small}
            alt={image.alt_description}
            onClick={() => openModal(image.urls.regular)}
            style={{
              width: "100%",
              height: "200px",
              objectFit: "cover",
              cursor: "pointer",
            }}
          />
        ))}
      </div>

      {/* Кнопка Load More */}
      {hasMore && !isLoading && (
        <div style={{ textAlign: "center", marginBottom: "20px" }}>
          <button
            onClick={loadMoreImages}
            style={{
              padding: "10px 20px",
              backgroundColor: "#3f51b5",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
              fontSize: "16px",
            }}
          >
            Load more
          </button>
        </div>
      )}

      {/* Спінер завантаження */}
      {isLoading && (
        <div style={{ textAlign: "center", margin: "20px 0" }}>
          <ThreeDots height="50" width="50" color="#3f51b5" />
        </div>
      )}

      {/* Модальне вікно */}
      <Modal
        isOpen={!!selectedImage}
        onRequestClose={closeModal}
        style={{
          overlay: { backgroundColor: "rgba(0, 0, 0, 0.8)" },
          content: {
            top: "50%",
            left: "50%",
            right: "auto",
            bottom: "auto",
            marginRight: "-50%",
            transform: "translate(-50%, -50%)",
            padding: "0",
            border: "none",
            backgroundColor: "transparent",
          },
        }}
      >
        {selectedImage && (
          <img src={selectedImage} alt="Large preview" style={{ maxWidth: "100%", maxHeight: "90vh" }} />
        )}
      </Modal>
    </div>
  );
}
