import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import { fetchImages } from "./services/unsplashApi";
import { ThreeDots } from "react-loader-spinner";
import "./App.css"; // Підключення CSS файлу

Modal.setAppElement("#root");

export default function App() {
  const [images, setImages] = useState([]);
  const [query, setQuery] = useState("");
  const [page, setPage] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [hasMore, setHasMore] = useState(false);

  useEffect(() => {
    if (!query) return;

    const fetchImageData = async () => {
      setIsLoading(true);
      const results = await fetchImages(query, page);
      if (results.length > 0) {
        setImages((prevImages) => [...prevImages, ...results]);
        setHasMore(true);
      } else {
        setHasMore(false);
      }
      setIsLoading(false);
    };

    fetchImageData();
  }, [query, page]);

  const handleSearch = (e) => {
    e.preventDefault();
    setImages([]);
    setPage(1);
    setHasMore(false);
    setQuery(e.target.elements.search.value.trim());
  };

  const loadMoreImages = () => {
    setPage((prevPage) => prevPage + 1);
  };

  const openModal = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  const closeModal = () => {
    setSelectedImage(null);
  };

  return (
    <div>
      <header>
        <form onSubmit={handleSearch}>
          <input type="text" name="search" placeholder="Search images..." required />
          <button type="submit">Search</button>
        </form>
      </header>

      <div className="gallery">
        {images.map((image) => (
          <img
            key={image.id}
            src={image.urls.small}
            alt={image.alt_description}
            onClick={() => openModal(image.urls.regular)}
          />
        ))}
      </div>

      {hasMore && !isLoading && (
        <div className="load-more">
          <button onClick={loadMoreImages}>Load more</button>
        </div>
      )}

      {isLoading && (
        <div className="loader">
          <ThreeDots height="50" width="50" color="#3f51b5" />
        </div>
      )}

      <Modal isOpen={!!selectedImage} onRequestClose={closeModal}>
        {selectedImage && <img src={selectedImage} alt="Large preview" />}
      </Modal>
    </div>
  );
}
