import React, { useState } from 'react';
import { fetchImages } from './unsplashApi';

const App = () => {
  const [query, setQuery] = useState(''); // Пошуковий запит
  const [images, setImages] = useState([]); // Список зображень
  const [error, setError] = useState(null); // Стан для обробки помилок
  const [isLoading, setIsLoading] = useState(false); // Стан завантаження

  const handleSearch = async () => {
    if (!query.trim()) return;

    setIsLoading(true);
    setError(null); // Скидаємо попередні помилки

    try {
      const results = await fetchImages(query, 1);
      setImages(results);
    } catch (err) {
      setError('Failed to fetch images. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h1>Image Search App</h1>
      <input
        type="text"
        placeholder="Search images..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        style={{ padding: '10px', width: '300px', marginRight: '10px' }}
      />
      <button onClick={handleSearch} style={{ padding: '10px' }}>
        Search
      </button>

      {isLoading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div style={{ display: 'flex', flexWrap: 'wrap', marginTop: '20px', gap: '10px', justifyContent: 'center' }}>
        {images.length > 0 ? (
          images.map((image) => (
            <img
              key={image.id}
              src={image.urls.small}
              alt={image.alt_description || 'Image'}
              style={{ width: '200px', height: '200px', objectFit: 'cover', borderRadius: '8px' }}
            />
          ))
        ) : (
          !isLoading && <p>No images found. Try another query.</p>
        )}
      </div>
    </div>
  );
};

export default App;
